const defaultState={inputValue:"dell",list:[]};export default(state=defaultState,action)=>{if("change_input_value"===action.type){const newState=JSON.parse(JSON.stringify(state));return newState.inputValue=action.inputValue,newState}if("add_item"===action.type){const newState=JSON.parse(JSON.stringify(state));return newState.list.push(newState.inputValue),newState.inputValue="",newState}if("delete_item"===action.type){const newState=JSON.parse(JSON.stringify(state));return newState.list.splice(action.index,1),newState}return state};